import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
  Button,
  IconButton,
  Typography,
  Snackbar,
  Alert,
  Divider
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';

const STORAGE_KEY = 'serverResourcesData';

const defaultRow = {
  serverName: '',
  cpuCores: '',
  memory: '',
  os: '',
  systemDisk: '',
  dataDisk: '',
  totalStorage: '',
  notes: ''
};

export default function ServerResources() {
  const [rows, setRows] = useState(() => {
    // 从localStorage读取初始数据
    const savedData = localStorage.getItem(STORAGE_KEY);
    return savedData ? JSON.parse(savedData) : [{ ...defaultRow }];
  });

  const [showSaveNotification, setShowSaveNotification] = useState(false);

  // 计算总计
  const calculateTotals = () => {
    return rows.reduce((acc, row) => ({
      cpuCores: acc.cpuCores + (parseInt(row.cpuCores) || 0),
      memory: acc.memory + (parseInt(row.memory) || 0),
      systemDisk: acc.systemDisk + (parseInt(row.systemDisk) || 0),
      dataDisk: acc.dataDisk + (parseInt(row.dataDisk) || 0),
      totalStorage: acc.totalStorage + (parseInt(row.totalStorage) || 0)
    }), {
      cpuCores: 0,
      memory: 0,
      systemDisk: 0,
      dataDisk: 0,
      totalStorage: 0
    });
  };

  // 生成配置说明
  const generateExplanation = (totals, servers) => {
    const serverTypes = servers.reduce((types, server) => {
      if (server.serverName && server.cpuCores && server.memory) {
        const type = `${server.cpuCores}核${server.memory}GB`;
        types[type] = (types[type] || 0) + 1;
      }
      return types;
    }, {});

    const serverTypeText = Object.entries(serverTypes)
      .map(([type, count]) => `${type} × ${count}台`)
      .join('、');

    return `
服务器资源配置说明：

1. 服务器配置概览
   - 服务器总数：${rows.filter(row => row.serverName).length}台
   - CPU总核数：${totals.cpuCores}核
   - 内存总容量：${totals.memory}GB
   - 存储总容量：${totals.totalStorage}GB（系统盘：${totals.systemDisk}GB，数据盘：${totals.dataDisk}GB）

2. 服务器类型分布
   ${serverTypeText || '暂无服务器配置'}

3. 存储配置说明
   - 系统盘：用于安装操作系统和应用软件
   - 数据盘：用于存储业务数据、日志、备份等

4. 配置建议
   - 建议系统盘容量不小于100GB，以确保足够的系统和应用安装空间
   - 建议预留30%的CPU和内存资源作为峰值缓冲
   - 建议数据盘根据实际业务数据量和增长预期进行配置
   - 建议选择主流的企业级操作系统，如CentOS、RedHat、Ubuntu Server等
   - 建议系统关键数据定期备份，建议备份保留时间不少于30天

5. 性能优化建议
   - 建议使用SSD作为系统盘，提升系统响应速度
   - 对于I/O密集型应用，建议使用高性能SSD作为数据盘
   - 建议根据实际业务负载情况，适当调整CPU和内存的配比
   - 建议配置合适的RAID级别，平衡数据安全性和性能需求

6. 可用性建议
   - 建议关键业务系统采用双机热备或集群部署
   - 建议配置UPS电源，防止意外断电
   - 建议服务器部署在不同机柜，降低单点故障风险
   - 建议实施完善的监控告警机制，及时发现和处理异常
`;
  };

  // 自动计算总存储并保存到localStorage
  useEffect(() => {
    const newRows = rows.map(row => ({
      ...row,
      totalStorage: ((parseInt(row.systemDisk) || 0) + (parseInt(row.dataDisk) || 0)).toString()
    }));
    
    if (JSON.stringify(newRows) !== JSON.stringify(rows)) {
      setRows(newRows);
    }

    // 保存到localStorage
    localStorage.setItem(STORAGE_KEY, JSON.stringify(newRows));
    setShowSaveNotification(true);
  }, [rows]);

  const handleChange = (index, field, value) => {
    const newRows = [...rows];
    newRows[index] = {
      ...newRows[index],
      [field]: value
    };
    setRows(newRows);
  };

  const addRow = () => {
    setRows([...rows, { ...defaultRow }]);
  };

  const deleteRow = (index) => {
    const newRows = rows.filter((_, i) => i !== index);
    setRows(newRows.length ? newRows : [{ ...defaultRow }]);
  };

  const handleCloseNotification = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setShowSaveNotification(false);
  };

  const clearAllData = () => {
    if (window.confirm('确定要清除所有数据吗？此操作不可恢复。')) {
      setRows([{ ...defaultRow }]);
      localStorage.removeItem(STORAGE_KEY);
    }
  };

  const totals = calculateTotals();

  return (
    <Box>
      <Box sx={{ mb: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h6">
          服务器资源配置
        </Typography>
        <Button
          variant="outlined"
          color="error"
          size="small"
          onClick={clearAllData}
        >
          清除所有数据
        </Button>
      </Box>
      
      <TableContainer component={Paper}>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>服务器名称</TableCell>
              <TableCell align="right">CPU核数</TableCell>
              <TableCell align="right">内存(GB)</TableCell>
              <TableCell>操作系统</TableCell>
              <TableCell align="right">系统盘(GB)</TableCell>
              <TableCell align="right">数据盘(GB)</TableCell>
              <TableCell align="right">总存储(GB)</TableCell>
              <TableCell>备注</TableCell>
              <TableCell align="center">操作</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row, index) => (
              <TableRow key={index}>
                <TableCell>
                  <TextField
                    size="small"
                    fullWidth
                    value={row.serverName}
                    onChange={(e) => handleChange(index, 'serverName', e.target.value)}
                  />
                </TableCell>
                <TableCell align="right">
                  <TextField
                    size="small"
                    type="number"
                    value={row.cpuCores}
                    onChange={(e) => handleChange(index, 'cpuCores', e.target.value)}
                    inputProps={{ style: { textAlign: 'right' } }}
                  />
                </TableCell>
                <TableCell align="right">
                  <TextField
                    size="small"
                    type="number"
                    value={row.memory}
                    onChange={(e) => handleChange(index, 'memory', e.target.value)}
                    inputProps={{ style: { textAlign: 'right' } }}
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    size="small"
                    fullWidth
                    value={row.os}
                    onChange={(e) => handleChange(index, 'os', e.target.value)}
                  />
                </TableCell>
                <TableCell align="right">
                  <TextField
                    size="small"
                    type="number"
                    value={row.systemDisk}
                    onChange={(e) => handleChange(index, 'systemDisk', e.target.value)}
                    inputProps={{ style: { textAlign: 'right' } }}
                  />
                </TableCell>
                <TableCell align="right">
                  <TextField
                    size="small"
                    type="number"
                    value={row.dataDisk}
                    onChange={(e) => handleChange(index, 'dataDisk', e.target.value)}
                    inputProps={{ style: { textAlign: 'right' } }}
                  />
                </TableCell>
                <TableCell align="right">
                  <TextField
                    size="small"
                    type="number"
                    value={row.totalStorage}
                    disabled
                    inputProps={{ style: { textAlign: 'right' } }}
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    size="small"
                    fullWidth
                    value={row.notes}
                    onChange={(e) => handleChange(index, 'notes', e.target.value)}
                  />
                </TableCell>
                <TableCell align="center">
                  <IconButton 
                    size="small" 
                    onClick={() => deleteRow(index)}
                    disabled={rows.length === 1}
                  >
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
            {/* 总计行 */}
            <TableRow>
              <TableCell>
                <Typography variant="subtitle2">总计</Typography>
              </TableCell>
              <TableCell align="right">
                <Typography variant="subtitle2">{totals.cpuCores}</Typography>
              </TableCell>
              <TableCell align="right">
                <Typography variant="subtitle2">{totals.memory}</Typography>
              </TableCell>
              <TableCell />
              <TableCell align="right">
                <Typography variant="subtitle2">{totals.systemDisk}</Typography>
              </TableCell>
              <TableCell align="right">
                <Typography variant="subtitle2">{totals.dataDisk}</Typography>
              </TableCell>
              <TableCell align="right">
                <Typography variant="subtitle2">{totals.totalStorage}</Typography>
              </TableCell>
              <TableCell />
              <TableCell />
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>

      <Box sx={{ mt: 2, display: 'flex', justifyContent: 'flex-end' }}>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={addRow}
        >
          添加服务器
        </Button>
      </Box>

      <Divider sx={{ my: 4 }} />

      <Paper elevation={1} sx={{ p: 3, mt: 3 }}>
        <Typography variant="h6" gutterBottom>
          配置说明
        </Typography>
        <Typography
          component="pre"
          sx={{
            whiteSpace: 'pre-wrap',
            fontFamily: 'inherit',
            fontSize: 'inherit'
          }}
        >
          {generateExplanation(totals, rows)}
        </Typography>
      </Paper>

      <Snackbar
        open={showSaveNotification}
        autoHideDuration={2000}
        onClose={handleCloseNotification}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert onClose={handleCloseNotification} severity="success" sx={{ width: '100%' }}>
          数据已自动保存
        </Alert>
      </Snackbar>
    </Box>
  );
}
