import React, { useState, useEffect } from 'react';
import {
  Grid,
  TextField,
  Typography,
  Button,
  Paper,
  Box
} from '@mui/material';
import {
  calculateTPCC,
  calculatePeakTPS
} from '../../services/performanceCalculator';

export default function TPCCCalculator({ onCalculate }) {
  const [inputs, setInputs] = useState({
    // TPS峰值计算参数
    totalUsers: 10000, // 总用户数
    concurrentPercentage: 0.1, // 并发用户百分比 (10%)
    transactionsPerMinute: 15, // 每个客户端每分钟处理事务数
    queriesPerTransaction: 5, // 每个事务查询次数

    // TPC-C计算参数
    m2: 5, // 联机事务处理TPC值 (5-15)
    m1: 0.6, // CPU信息量 (60%)
    m4: 1.3 // 预留扩展参数
  });

  const [result, setResult] = useState(null);

  useEffect(() => {
    calculateResults();
  }, [inputs]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setInputs(prev => ({
      ...prev,
      [name]: parseFloat(value) || 0
    }));
  };

  const calculateResults = () => {
    // 计算TPS峰值
    const peakTPS = calculatePeakTPS({
      totalUsers: inputs.totalUsers,
      concurrentPercentage: inputs.concurrentPercentage,
      transactionsPerMinute: inputs.transactionsPerMinute,
      queriesPerTransaction: inputs.queriesPerTransaction
    });

    // 计算TPC-C值
    const tpccResult = calculateTPCC({
      peakTPS,
      m2: inputs.m2,
      m1: inputs.m1,
      m4: inputs.m4
    });

    const finalResult = {
      peakTPS,
      tpccValue: tpccResult,
      explanation: generateExplanation(peakTPS, tpccResult, inputs)
    };

    setResult(finalResult);
    onCalculate(finalResult);
  };

  const generateExplanation = (peakTPS, tpccResult, params) => {
    return `3.1.2.1.3.2.2 数据库服务器（TPC-C）
数据库服务器主要处理数据访问、数据转换、数据存储，采用TPC-C值测算。计算公式为：TPM = TPS峰值 × M2 /（1 - M1）× M4。

其中：
- TPS峰值为系统访问量最大值
- M2为联机事务处理所对应的TPC值，取值范围5～15
- M1是留给CPU的信息量
- M4是预留未来数据库系统扩展的参数

计算过程：
1. TPS峰值计算：
   - 总用户数：${params.totalUsers}
   - 并发用户比例：${params.concurrentPercentage * 100}%
   - 每个客户端每分钟处理事务数：${params.transactionsPerMinute}
   - 每个事务查询次数：${params.queriesPerTransaction}
   - TPS峰值 = ${params.totalUsers} × ${params.concurrentPercentage} × ${params.transactionsPerMinute} × ${params.queriesPerTransaction} = ${peakTPS}

2. TPC-C值计算：
   - M2（联机事务处理TPC值）= ${params.m2}
   - M1（CPU信息量）= ${params.m1 * 100}%
   - M4（系统扩展参数）= ${params.m4}
   
最终TPC-C值 = ${peakTPS} × ${params.m2} / (1 - ${params.m1}) × ${params.m4} = ${tpccResult.toLocaleString()} tpmC`;
  };

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        TPC-C性能测算
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Paper elevation={2} sx={{ p: 2 }}>
            <Typography variant="subtitle1" gutterBottom>
              TPS峰值参数
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="总用户数"
                  name="totalUsers"
                  type="number"
                  value={inputs.totalUsers}
                  onChange={handleInputChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="并发用户百分比"
                  name="concurrentPercentage"
                  type="number"
                  value={inputs.concurrentPercentage}
                  onChange={handleInputChange}
                  helperText="0-1之间的小数，例如0.1表示10%"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="每分钟处理事务数"
                  name="transactionsPerMinute"
                  type="number"
                  value={inputs.transactionsPerMinute}
                  onChange={handleInputChange}
                  helperText="每个客户端每分钟处理的事务数"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="每事务查询次数"
                  name="queriesPerTransaction"
                  type="number"
                  value={inputs.queriesPerTransaction}
                  onChange={handleInputChange}
                />
              </Grid>
            </Grid>
          </Paper>
        </Grid>

        <Grid item xs={12} md={6}>
          <Paper elevation={2} sx={{ p: 2 }}>
            <Typography variant="subtitle1" gutterBottom>
              TPC-C参数
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="联机事务处理TPC值(M2)"
                  name="m2"
                  type="number"
                  value={inputs.m2}
                  onChange={handleInputChange}
                  helperText="取值范围5-15"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="CPU信息量(M1)"
                  name="m1"
                  type="number"
                  value={inputs.m1}
                  onChange={handleInputChange}
                  helperText="0-1之间的小数，例如0.6表示60%"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="预留扩展参数(M4)"
                  name="m4"
                  type="number"
                  value={inputs.m4}
                  onChange={handleInputChange}
                />
              </Grid>
            </Grid>
          </Paper>
        </Grid>

        <Grid item xs={12}>
          <Button
            variant="contained"
            color="primary"
            onClick={calculateResults}
            fullWidth
          >
            计算
          </Button>
        </Grid>

        {result && (
          <Grid item xs={12}>
            <Paper elevation={2} sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                计算结果
              </Typography>
              <Typography>
                TPS峰值: {result.peakTPS.toLocaleString()}
              </Typography>
              <Typography>
                TPC-C值: {result.tpccValue.toLocaleString()} tpmC
              </Typography>
              <Typography>
                {result.explanation}
              </Typography>
            </Paper>
          </Grid>
        )}
      </Grid>
    </Box>
  );
}
