import React, { useState } from 'react';
import {
  Box,
  Tab,
  Tabs,
  Typography,
  Paper,
  Container,
  Grid
} from '@mui/material';
import SPECjbbCalculator from './calculators/SPECjbbCalculator';
import TPCCCalculator from './calculators/TPCCCalculator';
import ResourceSummary from './ResourceSummary';
import PerformanceExplanation from './PerformanceExplanation';

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`calculator-tabpanel-${index}`}
      aria-labelledby={`calculator-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

export default function PerformanceCalculator() {
  const [tabValue, setTabValue] = useState(0);
  const [results, setResults] = useState({
    specjbb: null,
    tpcc: null
  });

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleSPECjbbResult = (result) => {
    setResults(prev => ({
      ...prev,
      specjbb: result
    }));
  };

  const handleTPCCResult = (result) => {
    setResults(prev => ({
      ...prev,
      tpcc: result
    }));
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper elevation={3} sx={{ p: 2 }}>
            <Typography variant="h4" component="h1" gutterBottom>
              性能测算工具
            </Typography>
            
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
              <Tabs value={tabValue} onChange={handleTabChange}>
                <Tab label="SPECjbb2005" />
                <Tab label="TPC-C" />
              </Tabs>
            </Box>

            <TabPanel value={tabValue} index={0}>
              <SPECjbbCalculator onCalculate={handleSPECjbbResult} />
            </TabPanel>
            
            <TabPanel value={tabValue} index={1}>
              <TPCCCalculator onCalculate={handleTPCCResult} />
            </TabPanel>

            {(results.specjbb || results.tpcc) && (
              <Box sx={{ mt: 4 }}>
                <ResourceSummary results={results} />
              </Box>
            )}
          </Paper>
        </Grid>

        <Grid item xs={12}>
          {(results.specjbb?.explanation || results.tpcc?.explanation) && (
            <PerformanceExplanation
              specjbbExplanation={results.specjbb?.explanation || '暂无SPECjbb2005测算数据'}
              tpccExplanation={results.tpcc?.explanation || '暂无TPC-C测算数据'}
            />
          )}
        </Grid>
      </Grid>
    </Container>
  );
}
